scripts.fetchUserProfile:
function(accessToken, ctx, cb) {  
  request.get('https://registry##PARATEXT_AUDIOMANAGER_REGISTRY_SUBDOMAIN_SUFFIX##.paratext.org/api8/userinfo', {
      headers: {
        'Authorization': 'Bearer ' + accessToken
      }
    },
    function(err, res, body) {
      if (err) {
        return cb(err);
      }

      if (res.statusCode !== 200) {
        return cb(new Error(body));
      }

      try {
        var profile = JSON.parse(body);
        profile.user_id = profile.sub;
        profile.name = profile.username;
        delete profile.sub;
        delete profile.pt_approved;
        delete profile.primary_org_id;
        cb(null, profile);
      } catch (e) {
        cb(e);
      }
    }
  );
}
